/**
 * Simple blur placeholder helper that works in all environments
 */

const FALLBACK_BLUR =
  "data:image/jpeg;base64,"

/**
 * Generate a simple blur placeholder
 * This version avoids all Node.js dependencies
 */
export async function getImagePlaceholder(src: string) {
  // For now, just return the fallback blur
  // In a real server environment, you could implement actual blur generation
  return { blurDataURL: FALLBACK_BLUR }
}

/**
 * Static placeholder for placeholder images
 */
export function getStaticImagePlaceholder() {
  return { blurDataURL: FALLBACK_BLUR }
}

/**
 * Generate different colored blur placeholders based on image type
 */
export function getColoredBlurPlaceholder(color: "blue" | "green" | "orange" | "pink" | "yellow" = "blue") {
  const blurs = {
    blue: "data:image/jpeg;base64,",
    green:
      "data:image/jpeg;base64,",
    orange:
      "data:image/jpeg;base64,",
    pink: "data:image/jpeg;base64,",
    yellow:
      "data:image/jpeg;base64,",
  }

  return { blurDataURL: blurs[color] }
}
